package pborifan;

public class KelilingLingkaran {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double d = 10;
		double phi = 3.14;
		double k = phi * d;
		
		System.out.println("Jadi, Keliling lingkaran dengan diameter " + d + " adalah " + k);
	}

}
