package pborifan;

public class VolumeTabung {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double d = 5;
		double t = 10;
		double phi = 3.14;
		double vol = (0.25 * phi * d * d * t); // 1/4 --> 0,25
		
		System.out.println("Jadi, Volume tabung dengan diameter " + d + " dan tinggi " + t + " adalah " + vol);
	}

}
